package com.mproduits;

import com.mproduits.model.Product;
import com.mproduits.repository.ProductRepository;
import org.springframework.boot.CommandLineRunner;
import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;
import org.springframework.cloud.netflix.eureka.EnableEurekaClient;
import org.springframework.cloud.netflix.hystrix.EnableHystrix;
import org.springframework.context.annotation.Bean;

@SpringBootApplication
@EnableEurekaClient
@EnableHystrix
public class MproduitsApplication {

	public static void main(String[] args) {
		SpringApplication.run(MproduitsApplication.class, args);
	}

	@Bean
	CommandLineRunner runner(ProductRepository productRepository){
		return args -> {
			productRepository.save(new Product(0, "Bougie fonctionnant au feu", "bougie qui fonctionne comme une ampoule mais sans électricité !", "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcRONgXkOJCHd9jFFbry9ycqDBDKSRsEhYr3wyzVE9wmepuztqSK", 22.0));

			productRepository.save(new Product(1, "Cheval pour nains", "Ce cheval ne portera certainement pas blanche neige, mais sans problème les nains", "data:image/jpeg;base64,", 360.0));

			productRepository.save(new Product(2,"Coq of steel, le superman des volailles", "Ne passe pas au four", "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcTaWJN5K7kkzILI4rfka96-MTFGk8kAzc0NYDnTNY8S-Abv28Bd", 620.0));

			productRepository.save(new Product(3, "Flacon à frotter avec un génie dedans", "Vous donne droit à l''équivalent de 3/0 voeux", "data:image/jpeg;base64,", 1200.0));

			productRepository.save(new Product(4, "Horloge quantique", "Donne l''heure, les minutes et même les secondes. Ne fait pas de café", "data:image/jpeg;base64,", 180.0));

			productRepository.save(new Product(5, "Table d' opération pour Hamsters", "Pour réaliser vos opérations chirugicales sur votre Hamster!", "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSywu_2o3Gju4jbnxSZUy30hdos6XEsJjHcy3Zv9GjsJ2p1mw9U", 210.0));

		};
	}
}
